new Vue({
    el: "#myHistorySwiper", // elid_namehtml: ''divid
    data: function () {
      return {
        swiperOption: {
          effect: "cube", // 
          loop: true, // 
          autoplay: {
            delay: 2500,
            disableOnInteraction: false,
          },
        },
        content: [],
      };
    },
    computed: {
      swiper() {
        return this.$refs.myhistoryswiper.$swiper;
      },
    },
    created() {
      this.getHistoryList();
    },
    methods: {
      // 
      stopAutoPlay() {
        this.swiperOption.autoplay && this.swiper.autoplay.stop();
      },
      // 
      startAutoPlay() {
        this.swiperOption.autoplay && this.swiper.autoplay.start();
      },
      // api, 
      getHistoryList() {
        fetch("https://tenapi.cn/lishi/?format=json", {
          method: "GET", // *GET, POST, PUT, DELETE, etc.
        })
          .then((res) => {
            return res.json();
          })
          .then((data) => {
            this.content = data.content;
          })
          .catch((err) => {
            console.log("err", err);
          });
      },
    },
  });

